import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Button, Badge, Alert } from 'react-bootstrap';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { experimentAPI } from '../services/api';
import { toast } from 'react-toastify';

const ExperimentDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [experiment, setExperiment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadExperiment();
  }, [id]);

  const loadExperiment = async () => {
    try {
      const response = await experimentAPI.getById(id);
      setExperiment(response.data);
    } catch (error) {
      setError('Failed to load experiment');
      toast.error('Failed to load experiment');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this experiment?')) {
      try {
        await experimentAPI.delete(id);
        toast.success('Experiment deleted successfully!');
        navigate('/');
      } catch (error) {
        toast.error('Failed to delete experiment');
      }
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      DRAFT: 'secondary',
      IN_PROGRESS: 'warning',
      COMPLETED: 'success',
      SUBMITTED: 'info',
      APPROVED: 'success',
      REJECTED: 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  if (loading) {
    return (
      <div className="text-center">
        <h3>Loading experiment...</h3>
      </div>
    );
  }

  if (error || !experiment) {
    return (
      <Alert variant="danger">
        {error || 'Experiment not found'}
        <Button as={Link} to="/" variant="outline-danger" className="ms-3">
          Back to Dashboard
        </Button>
      </Alert>
    );
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>🧪 Experiment Details</h2>
        <div>
          <Button
            as={Link}
            to={`/experiment/${id}/edit`}
            variant="primary"
            className="me-2"
          >
            ✏️ Edit
          </Button>
          <Button
            variant="danger"
            onClick={handleDelete}
          >
            🗑️ Delete
          </Button>
        </div>
      </div>

      <Card className="mb-4">
        <Card.Header>
          <div className="d-flex justify-content-between align-items-center">
            <h4>{experiment.title}</h4>
            {getStatusBadge(experiment.status)}
          </div>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={8}>
              <p className="text-muted">
                <strong>Created:</strong> {new Date(experiment.createdAt).toLocaleDateString()}
                {experiment.updatedAt && experiment.updatedAt !== experiment.createdAt && (
                  <span className="ms-3">
                    <strong>Updated:</strong> {new Date(experiment.updatedAt).toLocaleDateString()}
                  </span>
                )}
              </p>
            </Col>
            <Col md={4} className="text-end">
              <small className="text-muted">
                By: {experiment.userFullName || 'Unknown User'}
              </small>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      <Row>
        <Col lg={8}>
          <Card className="mb-4">
            <Card.Header>
              <h5>Objective</h5>
            </Card.Header>
            <Card.Body>
              <p>{experiment.objective || 'No objective specified.'}</p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Hypothesis</h5>
            </Card.Header>
            <Card.Body>
              <p>{experiment.hypothesis || 'No hypothesis specified.'}</p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Materials</h5>
            </Card.Header>
            <Card.Body>
              <p style={{ whiteSpace: 'pre-wrap' }}>
                {experiment.materials || 'No materials specified.'}
              </p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Procedure</h5>
            </Card.Header>
            <Card.Body>
              <p style={{ whiteSpace: 'pre-wrap' }}>
                {experiment.procedure || 'No procedure specified.'}
              </p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Observations</h5>
            </Card.Header>
            <Card.Body>
              <p style={{ whiteSpace: 'pre-wrap' }}>
                {experiment.observations || 'No observations recorded.'}
              </p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Results</h5>
            </Card.Header>
            <Card.Body>
              <p style={{ whiteSpace: 'pre-wrap' }}>
                {experiment.results || 'No results recorded.'}
              </p>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Header>
              <h5>Conclusion</h5>
            </Card.Header>
            <Card.Body>
              <p style={{ whiteSpace: 'pre-wrap' }}>
                {experiment.conclusion || 'No conclusion drawn.'}
              </p>
            </Card.Body>
          </Card>
        </Col>

        <Col lg={4}>
          <Card className="mb-4">
            <Card.Header>
              <h5>Experiment Info</h5>
            </Card.Header>
            <Card.Body>
              <p><strong>Status:</strong> {getStatusBadge(experiment.status)}</p>
              <p><strong>Created:</strong> {new Date(experiment.createdAt).toLocaleString()}</p>
              {experiment.experimentDate && (
                <p><strong>Experiment Date:</strong> {new Date(experiment.experimentDate).toLocaleDateString()}</p>
              )}
              <p><strong>Author:</strong> {experiment.userFullName || 'Unknown'}</p>
            </Card.Body>
          </Card>

          <Card>
            <Card.Header>
              <h5>Actions</h5>
            </Card.Header>
            <Card.Body>
              <Button
                as={Link}
                to={`/experiment/${id}/edit`}
                variant="primary"
                className="w-100 mb-2"
              >
                ✏️ Edit Experiment
              </Button>
              <Button
                as={Link}
                to="/"
                variant="secondary"
                className="w-100"
              >
                ← Back to Dashboard
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ExperimentDetail;
