import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Row, Col, Alert } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { experimentAPI } from '../services/api';
import { toast } from 'react-toastify';

const ExperimentForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [formData, setFormData] = useState({
    title: '',
    objective: '',
    hypothesis: '',
    materials: '',
    procedure: '',
    observations: '',
    results: '',
    conclusion: '',
    status: 'DRAFT'
  });

  useEffect(() => {
    if (id && id !== 'new') {
      loadExperiment();
    }
  }, [id]);

  const loadExperiment = async () => {
    try {
      const response = await experimentAPI.getById(id);
      setFormData(response.data);
    } catch (error) {
      toast.error('Failed to load experiment');
      navigate('/');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (id && id !== 'new') {
        await experimentAPI.update(id, formData);
        toast.success('Experiment updated successfully!');
      } else {
        await experimentAPI.create(formData, 1); // Using userId 1 for demo
        toast.success('Experiment created successfully!');
      }
      navigate('/');
    } catch (err) {
      setError(err.response?.data || 'Failed to save experiment');
      toast.error('Failed to save experiment');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="mb-4">
        {id && id !== 'new' ? '‚úèÔ∏è Edit Experiment' : 'üß™ New Experiment'}
      </h2>

      <Card>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={8}>
                <Form.Group className="mb-3">
                  <Form.Label>Title *</Form.Label>
                  <Form.Control
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                    placeholder="Enter experiment title"
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Status</Form.Label>
                  <Form.Select
                    name="status"
                    value={formData.status}
                    onChange={handleChange}
                  >
                    <option value="DRAFT">Draft</option>
                    <option value="IN_PROGRESS">In Progress</option>
                    <option value="COMPLETED">Completed</option>
                    <option value="SUBMITTED">Submitted</option>
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Objective</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="objective"
                value={formData.objective}
                onChange={handleChange}
                placeholder="What is the purpose of this experiment?"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Hypothesis</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                name="hypothesis"
                value={formData.hypothesis}
                onChange={handleChange}
                placeholder="What do you expect to happen?"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Materials</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="materials"
                value={formData.materials}
                onChange={handleChange}
                placeholder="List all materials and equipment needed"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Procedure</Form.Label>
              <Form.Control
                as="textarea"
                rows={5}
                name="procedure"
                value={formData.procedure}
                onChange={handleChange}
                placeholder="Step-by-step procedure"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Observations</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="observations"
                value={formData.observations}
                onChange={handleChange}
                placeholder="What did you observe during the experiment?"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Results</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="results"
                value={formData.results}
                onChange={handleChange}
                placeholder="What were the results?"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Conclusion</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="conclusion"
                value={formData.conclusion}
                onChange={handleChange}
                placeholder="What conclusions can you draw?"
              />
            </Form.Group>

            <div className="d-flex gap-2">
              <Button
                type="submit"
                variant="primary"
                disabled={loading}
              >
                {loading ? 'Saving...' : (id && id !== 'new' ? 'Update Experiment' : 'Create Experiment')}
              </Button>
              <Button
                variant="secondary"
                onClick={() => navigate('/')}
              >
                Cancel
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default ExperimentForm;
