import React, { useEffect, useState } from 'react';
import { experimentAPI } from '../services/api';
import { useSearch } from '../context/SearchContext';

const Dashboard = () => {
  const [experiments, setExperiments] = useState([]); // Ensure initial state is array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { searchTerm } = useSearch();

  useEffect(() => {
    experimentAPI.getAll()
      .then(data => {
        setExperiments(Array.isArray(data) ? data : []); // Defensive: always set array
        setLoading(false);
      })
      .catch(err => {
        setExperiments([]); // Set to empty array on error
        setError('Failed to fetch experiments');
        setLoading(false);
      });
  }, []);

  // Defensive check before filtering
  const filteredExperiments = Array.isArray(experiments)
    ? experiments.filter(exp =>
        exp.title && exp.title.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : [];

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Experiments</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Status</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredExperiments.map(exp => (
            <tr key={exp.id}>
              <td>{exp.title}</td>
              <td>{exp.status}</td>
              <td>{exp.experiment_date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
