import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Button, Badge, Form, InputGroup, Spinner, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { experimentAPI } from '../services/api';

const Dashboard = () => {
  const [experiments, setExperiments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchExperiments = async () => {
      try {
        setLoading(true);
        const response = await experimentAPI.getAll();
        setExperiments(response.data);
      } catch (err) {
        setError('Failed to fetch experiments.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchExperiments();
  }, []);

  const getStatusBadge = (status) => {
    const variants = {
      DRAFT: 'secondary',
      IN_PROGRESS: 'warning',
      COMPLETED: 'success',
      SUBMITTED: 'info',
      APPROVED: 'primary',
      REJECTED: 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const filteredExperiments = experiments.filter(exp =>
    exp.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (exp.objective && exp.objective.toLowerCase().includes(searchTerm.toLowerCase()))
  );
  
  const renderContent = () => {
    if (loading) {
      return (
        <div className="text-center">
          <Spinner animation="border" />
          <p>Loading experiments...</p>
        </div>
      );
    }

    if (error) {
      return <Alert variant="danger">{error}</Alert>;
    }

    if (filteredExperiments.length === 0) {
      return (
        <Card className="text-center p-5">
          <Card.Body>
            <h4>No experiments found</h4>
            <p className="text-muted">
              {searchTerm ? 'Try adjusting your search terms.' : 'Start by creating your first experiment!'}
            </p>
            {!searchTerm && (
              <Button as={Link} to="/experiment/new" variant="primary">
                Create First Experiment
              </Button>
            )}
          </Card.Body>
        </Card>
      );
    }

    return (
      <Row>
        {filteredExperiments.map((experiment) => (
          <Col key={experiment.id} lg={4} md={6} className="mb-4">
            <Card className="h-100">
              <Card.Body>
                <div className="d-flex justify-content-between align-items-start mb-2">
                  <h5 className="card-title">{experiment.title}</h5>
                  {getStatusBadge(experiment.status)}
                </div>
                <p className="card-text text-muted">
                  {experiment.objective?.substring(0, 100)}
                  {experiment.objective?.length > 100 && '...'}
                </p>
                <div className="d-flex justify-content-between align-items-center">
                  <small className="text-muted">
                    {new Date(experiment.createdAt).toLocaleDateString()}
                  </small>
                  <Button
                    as={Link}
                    to={`/experiment/${experiment.id}`}
                    variant="outline-primary"
                    size="sm"
                  >
                    View Details
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    );
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>🧪 My Experiments</h2>
        <Button as={Link} to="/experiment/new" variant="primary">
          + New Experiment
        </Button>
      </div>

      {/* Animated Search Bar for searching experiments */}
      <div className="search-bar-animated mb-4">
        <input
          type="checkbox"
          id="search-trigger"
          className="search-bar__checkbox"
        />
        <label
          className="search-bar__label-init"
          htmlFor="search-trigger"
        ></label>
        <label
          className="search-bar__label-active"
          htmlFor="search-trigger"
        ></label>
        <div className="search-bar__border"></div>
        <input
          type="text"
          className="search-bar__input"
          placeholder="Search experiments..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="search-bar__close"></div>
      </div>
      {/* End Animated Search Bar */}
      
      {renderContent()}
    </div>
  );
};

export default Dashboard;
