import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { FaBook, FaPlusSquare, FaUser, FaSignInAlt, FaSignOutAlt } from 'react-icons/fa';
import '../styles/App.css';
import { useSearch, useExperimentEvents } from '../context/SearchContext';

const NavigationBar = () => {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const { searchTerm, setSearchTerm } = useSearch();
  const { triggerRefresh } = useExperimentEvents();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="animated-navbar">
      <ul className="animated-nav-list">
        <li>
          <Link to="/">
            <FaBook style={{ marginRight: 6 }} /> Dashboard
          </Link>
        </li>
        {isAuthenticated && (
          <>
            <li>
              <Link to="/experiment/new">
                <FaPlusSquare style={{ marginRight: 6 }} /> New Experiment
              </Link>
            </li>
            <li>
              <Link to="/profile">
                <FaUser style={{ marginRight: 6 }} /> Profile
              </Link>
            </li>
            <li>
              <a href="#logout" onClick={handleLogout}>
                <FaSignOutAlt style={{ marginRight: 6 }} /> Logout
              </a>
            </li>
          </>
        )}
        {!isAuthenticated && (
          <li>
            <Link to="/login">
              <FaSignInAlt style={{ marginRight: 6 }} /> Login
            </Link>
          </li>
        )}
      </ul>
      {/* Restore animated search bar UI */}
      <div className="search-bar-animated">
        <input type="checkbox" id="search-trigger" className="search-bar__checkbox" />
        <label className="search-bar__label-init" htmlFor="search-trigger"></label>
        <label className="search-bar__label-active" htmlFor="search-trigger"></label>
        <div className="search-bar__border"></div>
        <input
          type="text"
          className="search-bar__input"
          placeholder="Search experiments..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <div className="search-bar__close"></div>
      </div>
    </nav>
  );
};

export default NavigationBar;
