import React, { useEffect, useState } from 'react';
import { experimentAPI } from '../services/api';
import { useSearch, useExperimentEvents } from '../context/SearchContext';
import { Card, Row, Col, Badge, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [experiments, setExperiments] = useState([]); // Ensure initial state is array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deletingId, setDeletingId] = useState(null);
  const { searchTerm } = useSearch();
  const { refreshCount } = useExperimentEvents();

  const fetchExperiments = () => {
    setLoading(true);
    experimentAPI.getAll()
      .then(response => {
        const data = response.data;
        setExperiments(Array.isArray(data) ? data : []); // Defensive: always set array
        setLoading(false);
      })
      .catch(err => {
        setExperiments([]); // Set to empty array on error
        setError('Failed to fetch experiments');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchExperiments();
    // eslint-disable-next-line
  }, [refreshCount]);

  // Defensive check before filtering
  const filteredExperiments = Array.isArray(experiments)
    ? experiments.filter(exp =>
        exp.title && exp.title.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : [];

  const handleDelete = async (id) => {
    setDeletingId(id);
    setError('');
    try {
      await experimentAPI.delete(id);
      fetchExperiments();
    } catch (err) {
      setError('Failed to delete experiment');
    } finally {
      setDeletingId(null);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const getStatusBadge = (status) => {
    const variants = {
      DRAFT: 'secondary',
      IN_PROGRESS: 'warning',
      COMPLETED: 'success',
      SUBMITTED: 'info',
      APPROVED: 'primary',
      REJECTED: 'danger'
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  return (
    <div>
      <h2 className="mb-4">Experiments</h2>
      <Row xs={1} md={2} lg={3} className="g-4">
        {filteredExperiments.map(exp => (
          <Col key={exp.id}>
            <Card className="h-100 shadow-sm border-0" style={{ background: 'linear-gradient(135deg, #e0e7ff 0%, #f8fafc 100%)' }}>
              <Card.Body>
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <Card.Title className="fw-bold" style={{ color: '#2d3a4b' }}>{exp.title}</Card.Title>
                  {getStatusBadge(exp.status)}
                </div>
                <Card.Text>
                  <strong>Date:</strong> {exp.experiment_date ? new Date(exp.experiment_date).toLocaleDateString() : 'N/A'}
                </Card.Text>
                <div className="d-flex justify-content-end gap-2 mt-3">
                  <Button
                    as={Link}
                    to={`/experiment/${exp.id}/edit`}
                    variant="outline-primary"
                    size="sm"
                  >
                    Edit
                  </Button>
                  <Button
                    onClick={() => handleDelete(exp.id)}
                    disabled={deletingId === exp.id}
                    variant="outline-danger"
                    size="sm"
                  >
                    {deletingId === exp.id ? 'Deleting...' : 'Delete'}
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Dashboard;
